import { animate, state, style, transition, trigger } from '@angular/animations';
import { DOCUMENT } from '@angular/common';
import { AfterViewInit, Component, HostListener, Inject, OnDestroy, OnInit, ViewChild } from '@angular/core';
import { Subscription, interval } from 'rxjs';
import {ElementRef, Renderer2 } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Form } from 'src/app/models/form';
import * as mailjet from 'node-mailjet';


@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
  animations: [
    trigger('slideInOut', [
      state('in', style({
        transform: 'translateY(0%)',
        opacity: '1'
      })),
      state('out', style({
        transform: 'translateY(-100%)',
        opacity: '0'
      })),
      transition('in => out', animate('500ms ease-in-out')),
      transition('out => in', animate('500ms ease-in-out'))
    ])
  ]
})
export class HomeComponent implements OnInit, AfterViewInit, OnDestroy {

  @ViewChild('section') section: ElementRef | undefined;

  isCollapsed = false;
  showContactButton = false;
  showHeading = false;
  showTyping = false;
  showNav = false;
  showSuccess!: boolean;
  public isVisible: boolean[] = [false, false, false];
  interval = 15; 
  private subscription: Subscription = new Subscription();
  typedMessage = '';
  slideIndex = 0;
  showScrollTop = false;
  contactForm!: FormGroup;
  form!:Form;
  message = "From our first hello to forever, our love grows like something rare and beautiful. Every day with you feels like day one—fresh, exciting, and full of possibility. As our love takes flight in 2025, we invite you to join us as we celebrate the beginning of our greatest adventure together. #everydayisdayone #loveInVAir25"

  constructor(
    @Inject(DOCUMENT) private document: Document, 
    private elementRef: ElementRef, 
    private renderer: Renderer2, 
    private formBuilder: FormBuilder
    ) {}

  ngOnInit(): void {
    setTimeout(() => {
      this.showContactButton = true;
    }, 5500);
    setTimeout(() => {
      this.showHeading = true;
    }, 1000);
    setTimeout(() => {
      this.showTyping = true;
    }, 1500);
    const messageLength = this.message.length;
    let i = 0;
    this.subscription = interval(this.interval).subscribe(() => {
      if (i <= messageLength) {
        this.typedMessage = this.message.slice(0, i);
        i++;
      } else {
        this.subscription.unsubscribe();
      }
    });

    const observer = new IntersectionObserver(entries => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          this.renderer.addClass(entry.target, 'fade-in');
        }
      });
    }, { rootMargin: '0px', threshold: 0.25 });

    const targets = this.elementRef.nativeElement.querySelectorAll('.section');

    targets.forEach((target: Element) => {
      observer.observe(target);
    });

    this.form = {} as Form;

    this.contactForm = this.formBuilder.group({
      firstName: ['', Validators.required],
      lastName: ['', Validators.required],
      emailAddress: ['', [Validators.required, Validators.email]],
      phoneNumber: ['', [Validators.required, Validators.pattern(/^(\+?\d{1,3}[- ]?)?\d{10}$/)]],
      message: ['', [Validators.required]]
    });
  }

  ngAfterViewInit(): void {
    const sectionObserver = new IntersectionObserver((entries, observer) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          entry.target.classList.add('fade-in');
          observer.unobserve(entry.target);
        }
      });
    }, { threshold: 0.5 });

    sectionObserver.observe(this.section!.nativeElement);
  }

  submitForm(): void {
    this.showSuccess = true;
    setTimeout(() => {
      this.showSuccess = false;
    }, 5000);
    this.contactForm.reset();
  }

  ngOnDestroy(): void {
    this.subscription.unsubscribe();
  }

  scrollTo(id: string): void {
    const element = this.document.getElementById(id)!;
    const topOffset = 55; 
    const elementPosition = element.getBoundingClientRect().top + window.pageYOffset - topOffset;
    const duration = 300; 
    const startTime = performance.now();
    const easing = (t: number) => t * t;
    const animation = (currentTime: number) => {
      const timeElapsed = currentTime - startTime;
      const scrollTop = easeInOut(easing, timeElapsed, window.pageYOffset, elementPosition - window.pageYOffset, duration);
      window.scrollTo({
        top: scrollTop,
        behavior: 'smooth',
      });
      if (timeElapsed < duration) {
        requestAnimationFrame(animation);
      }
    };
    requestAnimationFrame(animation);
    const easeInOut = (easing: (t: number) => number, t: number, b: number, c: number, duration: number): number => {
      t /= duration / 2;
      if (t < 1) return (c / 2) * easing(t) + b;
      t--;
      return (-c / 2) * (t * (t - 2) - 1) + b;
    };
    this.isCollapsed = !this.isCollapsed;
  }

  scrollTo2(id: string): void {
    const element = this.document.getElementById(id)!;
    const topOffset = 55; 
    const elementPosition = element.getBoundingClientRect().top + window.pageYOffset - topOffset;
    const duration = 300; 
    const startTime = performance.now();
    const easing = (t: number) => t * t;
    const animation = (currentTime: number) => {
      const timeElapsed = currentTime - startTime;
      const scrollTop = easeInOut(easing, timeElapsed, window.pageYOffset, elementPosition - window.pageYOffset, duration);
      window.scrollTo({
        top: scrollTop,
        behavior: 'smooth',
      });
      if (timeElapsed < duration) {
        requestAnimationFrame(animation);
      }
    };
    requestAnimationFrame(animation);
    const easeInOut = (easing: (t: number) => number, t: number, b: number, c: number, duration: number): number => {
      t /= duration / 2;
      if (t < 1) return (c / 2) * easing(t) + b;
      t--;
      return (-c / 2) * (t * (t - 2) - 1) + b;
    };
  }

toggleMenu() {
  this.isCollapsed = !this.isCollapsed;
}

@HostListener('window:scroll', ['$event'])
checkScroll() {
  const scrollPos = window.pageYOffset || document.documentElement.scrollTop || document.body.scrollTop || 0;
  this.showScrollTop = scrollPos > 0;
}
}


<script src="../../../app.component.ts"></script>
<script src="home.component.ts"></script>
<link rel="stylesheet" href="home.component.css">
<div class="bg-white">
  <!-- Header -->
  <header class="absolute inset-x-0 top-0 z-50">
    <nav
      class="section z-50 mx-auto bg-white flex items-center justify-between p-6 lg:px-8"
      aria-label="Global"
    >
      <div class="section flex lg:flex-1">
        <a href="#section1" class="-m-1.5 p-1.5">
          <span class="sr-only">Our Wedding</span>
          <img class="section h-20 w-auto" src="./assets/img/logo.png" alt="loveInVAir" />
        </a>
      </div>
      <div
        id="contact-button"
        class="section flex lg:hidden"
      >
        <button
          (click)="toggleMenu()"
          type="button"
          class="-m-2.5 inline-flex items-center justify-center rounded-md p-2.5 text-gray-900"
        >
          <svg
            class="h-10 w-10"
            fill="none"
            viewBox="0 0 24 24"
            stroke-width="1.5"
            stroke="black"
            aria-hidden="true"
          >
            <path
              stroke-linecap="round"
              stroke-linejoin="round"
              d="M3.75 6.75h16.5M3.75 12h16.5m-16.5 5.25h16.5"
            />
          </svg>
        </button>
      </div>
      <div

        id="contact-button"
        class="section hidden lg:flex lg:gap-x-12"
      >
        <a
          href="#section1"
          (click)="scrollTo('section1')"
          class="text-base text-gray-800 font-semibold rounded-sm px-3.5 py-2.5 border-b-4 hover:border-red-400 border-t-4 border-red-700 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 hover:scale-125 transition duration-500"
          >Schedule</a
        >
        <a
          href="#section3"
          (click)="scrollTo('section3')"
          class="text-base text-gray-800 font-semibold rounded-sm px-3.5 py-2.5 border-b-4 border-t-4 hover:border-red-400 border-red-700 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 hover:scale-125 transition duration-500"
          >Harmony</a
        >
        <a
          href="#section0"
          (click)="scrollTo('section2')"
          class="text-base text-gray-800 font-semibold rounded-sm px-3.5 py-2.5 border-b-4 border-t-4 hover:border-red-400 border-red-700 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 hover:scale-125 transition duration-500"
          >About Us</a
        >
        <a
          href="#section5"
          (click)="scrollTo('section5')"
          class="text-base text-gray-800 font-semibold rounded-sm px-3.5 py-2.5 border-b-4 hover:border-red-400 border-t-4 border-red-700 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 hover:scale-125 transition duration-500"
          >Contact Us</a
        >
        <a
        href="#section6"
        (click)="scrollTo('section6')"
        class="text-base text-gray-800 font-semibold rounded-sm px-3.5 py-2.5 border-b-4 hover:border-red-400 border-t-4 border-red-700 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 hover:scale-125 transition duration-500"
        >Work For Us</a
      >
      </div>
    </nav>
    <!-- Mobile menu, show/hide based on menu open state. -->
    <div class="lg:hidden" role="dialog" aria-modal="true">
      <!-- Background backdrop, show/hide based on slide-over state. -->
      <div
        [@slideInOut]="isCollapsed ? 'in' : 'out'"
        class="absolute inset-y-0 right-0 z-50 w-full h-96 bg-white px-6 py-6 sm:max-w-sm sm:ring-1 sm:ring-gray-900/10"
      >
        <div class="flex items-center justify-between">
          <a href="#section1" class="-m-1.5 p-1.5">
            <span class="sr-only">Your Company</span>
            <img class="h-14 w-28 -ml-6" src="./assets/img/logo.png" alt="Jarek Farm Logo" />
          </a>
          <button
            (click)="toggleMenu()"
            type="button"
            class="-m-2.5 rounded-md p-2.5 text-gray-800"
          >
            <span class="sr-only">Close menu</span>
            <svg
              class="h-10 w-10"
              fill="none"
              viewBox="0 0 24 24"
              stroke-width="1.5"
              stroke="currentColor"
              aria-hidden="true"
            >
              <path
                stroke-linecap="round"
                stroke-linejoin="round"
                d="M6 18L18 6M6 6l12 12"
              />
            </svg>
          </button>
        </div>
        <div class="-my-4 pt-4 divide-y divide-gray-500/10">
          <div class="space-y-2 py-4">
            <a
              href="#section1"
              (click)="scrollTo('section1')"
              class="-mx-3 block rounded-lg px-3 py-2 text-base font-bold leading-7 text-gray-900 hover:bg-gray-200"
              >Home</a
            >
            <a
              href="#section3"
              (click)="scrollTo('section3')"
              class="-mx-3 block rounded-lg px-3 py-2 text-base font-bold leading-7 text-gray-900 hover:bg-gray-200"
              >Products</a
            >
            <a
              href="#section2"
              (click)="scrollTo('section2')"
              class="-mx-3 block rounded-lg px-3 py-2 text-base font-bold leading-7 text-gray-900 hover:bg-gray-200"
              >About Us</a
            >
            <a
              href="#section5"
              (click)="scrollTo('section5')"
              class="-mx-3 block rounded-lg px-3 py-2 text-base font-bold leading-7 text-gray-900 hover:bg-gray-200"
              >Contact Us</a
            >
            <a
            href="#section6"
            (click)="scrollTo('section6')"
            class="-mx-3 block rounded-lg px-3 py-2 text-base font-bold leading-7 text-gray-900 hover:bg-gray-200"
            >Work For Us</a
          >
          </div>
        </div>
      </div>
    </div>
  </header>

  <main>
    <!-- Home section1 -->
    <div
      id="section1"
      class="relative isolate overflow-hidden bg-gray-900 pb-16 pt-14 sm:pb-20"
      style="height: 1000px"
    >
      <img
        src="./assets/img/homePage.jpg"
        alt=""
        class="absolute inset-0 -z-10 h-full w-full object-cover inset-y-0 right-0"
      />
      <div class="mx-auto max-w-7xl px-6 lg:px-8">
        <div class="mx-auto max-w-2xl py-32 sm:py-48 lg:py-56">
          <div class="text-center">
            <h1
              [ngClass]="{
                'header-enter': !showContactButton,
                'header-enter-active': showHeading
              }"
              id="contact-button"
              class="text-4xl py-6 font-bold tracking-tight text-white sm:text-6xl"
            >
              Forever Begins Here: Celebrating Our Love Story
            </h1>
            <p
              [ngClass]="{
                'header-enter': !showContactButton,
                'header-enter-active': showTyping
              }"
              id="contact-button"
              class="mt-4 pt-4 text-lg font-semibold leading-8 text-white"
            >
              {{ typedMessage }}
            </p>
            <div
              class="mt-16 flex items-center justify-center gap-x-6 contact-button"
              [ngClass]="{
                'contact-button-enter': !showContactButton,
                'contact-button-enter-active': showContactButton
              }"
              id="contact-button"
            >
              <a
                href="#section5"
                (click)="scrollTo2('section5')"
                class="rounded-md bg-red-700 px-3.5 py-2.5 text-md font-semibold text-white shadow-sm hover:bg-red-600 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-red-400 animate-pulse"
                >Contact Us Today</a
              >
              <a
                href="#section3"
                (click)="scrollTo2('section3')"
                class="text-base font-semibold leading-6 text-white hover:scale-125 transition duration-500"
                >Products <span aria-hidden="true">→</span></a
              >
            </div>
          </div>
        </div>
      </div>
    </div>

    <!-- About section2 -->
    <div class="mt-32 sm:mt-56">
      <div class="section mx-auto max-w-7xl px-6 lg:px-8">
        <div
          class="absolute inset-x-0 -top-3 -z-10 transform-gpu overflow-hidden px-36 blur-3xl"
          aria-hidden="true"
        >
          <div
            class="mx-auto aspect-[1155/678] w-[72.1875rem] bg-gradient-to-tr from-[#FFFF00] to-[#FFFF00] opacity-20"
            style="
              clip-path: polygon(
                74.1% 44.1%,
                100% 61.6%,
                97.5% 26.9%,
                85.5% 0.1%,
                80.7% 2%,
                72.5% 32.5%,
                60.2% 62.4%,
                52.4% 68.1%,
                47.5% 58.3%,
                45.2% 34.5%,
                27.5% 76.7%,
                0.1% 64.9%,
                17.9% 100%,
                27.6% 76.8%,
                76.1% 97.7%,
                74.1% 44.1%
              );
            "
          ></div>
        </div>
        <div id="section2" class="mx-auto max-w-2xl sm:text-center">
          <img
          class="h-20 w-auto mx-auto items-center justify-center"
          src="./assets/img/proudlynigeria.png"
          alt=""
        />
          <h2
            class="mt-2 text-3xl font-bold tracking-tight text-gray-900 sm:text-4xl"
          >
            About Us
          </h2>
          <p class="mt-6 text-lg leading-8 text-gray-600">
            Welcome to Jarek Farms, a Nigerian-registered company with a
            mission to improve essential food supplies for the people of Nigeria
            and to create employment and training opportunities for Nigerians.
            Our visionary group of Nigerian and UK nationals is committed to
            making a positive impact in Nigeria's agricultural sector. We have
            successfully launched our poultry business, and we are now expanding
            organically into other areas of agricultural need in Nigeria. Our
            goal is to help reduce national shortages of essential food
