<!--
  Success toast, show/hide based on toasts state.
-->
<div *ngIf="showSuccess" class=" pointer-events-none fixed inset-x-0 bottom-0 sm:flex sm:justify-center sm:px-6 sm:pb-5 lg:px-8">
  <div class="pointer-events-auto flex items-center justify-between gap-x-6 bg-green-700 px-6 py-2.5 sm:rounded-xl sm:py-3 sm:pl-4 sm:pr-3.5">
    <p class="text-sm leading-6 text-white">
      <a href="#">
        <strong class="font-semibold">Message Sent Successfully</strong><svg viewBox="0 0 2 2" class="mx-2 inline h-0.5 w-0.5 fill-current" aria-hidden="true"><circle cx="1" cy="1" r="1" /></svg>Your message has been sent and we will get back to you shortly&nbsp;<span aria-hidden="true"></span>
      </a>
    </p>
  </div>
