              method="POST"
              [formGroup]="contactForm"
              class="section px-6 pb-20 pt-20 sm:pb-32 lg:px-8 lg:py-48"
            >
              <div class="mx-auto max-w-xl lg:mr-0 lg:max-w-lg">
                <div class="grid grid-cols-1 gap-x-8 gap-y-6 sm:grid-cols-2">
                  <div>
                    <label
                      for="firstName"
                      class="block text-sm font-semibold leading-6 text-gray-900"
                      >First name</label
                    >
                    <div class="mt-2.5">
                      <input
                        formControlName="firstName"
                        type="text"
                        name="first-name"
                        id="first-name"
                        autocomplete="given-name"
                        placeholder="John"
                        class="block w-full rounded-md border-2 hover:border-[#7851A9] px-3.5 py-2 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-[#7851A9] sm:text-sm sm:leading-6"
                      />
                    </div>
                    <p
                      *ngIf="
                        contactForm.controls['firstName'].invalid &&
                        contactForm.controls['firstName'].touched
                      "
                      class="text-xs text-[#7851A9] text-right italic font-semibold"
                    >
                      *Field Required
                    </p>
                  </div>
                  <div>
                    <label
                      for="lastName"
                      class="block text-sm font-semibold leading-6 text-gray-900"
                      >Last name</label
                    >
                    <div class="mt-2.5">
                      <input
                        formControlName="lastName"
                        type="text"
                        name="last-name"
                        id="last-name"
                        autocomplete="family-name"
                        placeholder="Doe"
                        class="block w-full rounded-md border-2 hover:border-[#7851A9] px-3.5 py-2 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-[#7851A9] sm:text-sm sm:leading-6"
                      />
                    </div>
                  </div>
                  <div class="sm:col-span-2">
                    <label
                      for="emailAddress"
                      class="block text-sm font-semibold leading-6 text-gray-900"
                      >Email</label
                    >
                    <div class="mt-2.5">
                      <input
                        formControlName="emailAddress"
                        type="email"
                        name="email"
                        id="email"
                        autocomplete="email"
                        placeholder="example@mail.com"
                        class="block w-full rounded-md border-2 hover:border-[#7851A9] px-3.5 py-2 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-[#7851A9] sm:text-sm sm:leading-6"
                      />
                    </div>
                    <p
                      *ngIf="
                        contactForm.controls['emailAddress'].invalid &&
                        contactForm.controls['emailAddress'].touched
                      "
                      class="text-xs text-[#7851A9] text-right italic font-semibold"
                    >
                      *Please enter a valid email address
                    </p>
                  </div>
                  <div class="sm:col-span-2">
                    <label
                      for="phoneNumber"
                      class="block text-sm font-semibold leading-6 text-gray-900"
                      >Phone number</label
                    >
                    <div class="mt-2.5">
                      <input
                      formControlName="phoneNumber"
                        type="tel"
                        name="phone-number"
                        id="phone-number"
                        autocomplete="tel"
                        placeholder="+234123456789"
                        class="block w-full rounded-md border-2 hover:border-[#7851A9] px-3.5 py-2 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-[#7851A9] sm:text-sm sm:leading-6"
                      />
                    </div>
                    <p
                      *ngIf="
                        contactForm.controls['phoneNumber'].invalid &&
                        contactForm.controls['phoneNumber'].touched
